INSERT INTO `s_marketingstatement` VALUES (1,NULL,'<p> </p>\r\n<ul>\r\n<li>\r\n<p class=\"western\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\"><font size=\"3\">Concentrate (Concentrated)</font></font></p>\r\n</li>\r\n<li>\r\n<p class=\"western\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\"><font size=\"3\">Neutral (Neutral pH)</font></font></p>\r\n</li>\r\n<li>\r\n<p class=\"western\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\"><font size=\"3\">Disinfects (Disinfectant)</font></font></p>\r\n</li>\r\n<li>\r\n<p class=\"western\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\"><font size=\"3\">Cleans (Cleaner)</font></font></p>\r\n</li>\r\n<li>\r\n<p class=\"western\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\"><font size=\"3\">Deodorizes (Deodorizer)</font></font></p>\r\n</li>\r\n</ul>\r\n<p> </p>','R',NULL,1,NULL,1,NULL),(2,NULL,'<p><font face=\"Times New Roman, serif\"><font size=\"3\"><font face=\"Arial, sans-serif\">A multi-purpose, neutral pH, germicidal detergent and deodorant effective in hard water up to 400 ppm (calculated as CaCO</font><sub><font face=\"Arial, sans-serif\">3</font></sub><font face=\"Arial, sans-serif\">) in the presence of a moderate amount of soil (5% organic serum) according to the AOAC Use-dilution Test. Disinfects, cleans, and deodorizes in one labor-saving step.</font></font></font></p>','R',NULL,2,NULL,1,NULL),(3,NULL,'<p><font size=\"2\">Recommended for use in hospitals, nursing homes, schools, colleges, commercial and industrial institutions, office buildings, veterinary clinics, animal life science laboratories, zoos, federally inspected meat and poultry plants, equine farms, tack shops, pet shops, airports, kennels, hotels, motels, poultry farms, turkey farms, dairy farms, hog farms, breeding establishments, grooming establishments, funeral homes, and households. Use to clean and disinfect non-medical (i.e. industrial and fire-fighting) respirators in industrial, commercial and institutional premises.</font></p>','R',NULL,3,NULL,1,NULL),(4,NULL,'<p><font face=\"Times New Roman, serif\"><font size=\"3\"><font face=\"Arial, sans-serif\">Disinfects, cleans, and deodorizes the following hard nonporous inanimate surfaces: floors, walls, (</font><font face=\"Arial, sans-serif\"><em>non-medical)</em></font><font face=\"Arial, sans-serif\">metal surfaces, (</font><font face=\"Arial, sans-serif\"><em>non-medical)</em></font><font face=\"Arial, sans-serif\">stainless steel surfaces, glazed porcelain, plastic surfaces (such as polypropylene, polystyrene, etc.).</font></font></font></p>','R',NULL,4,NULL,1,NULL),(5,NULL,'<p><font face=\"Arial, sans-serif\"><font size=\"3\">Use [on] [to clean and disinfect] nonporous athletic mats, wrestling mats, gymnastic mats, exercise equipment and training tables.</font></font></p>','R',NULL,5,NULL,1,NULL),(6,NULL,'<p><font face=\"Arial, sans-serif\"><font size=\"3\">Use [on] [to clean and disinfect] nonporous personal protective safety equipment, wrestling headgear, boxing headgear, protective headgear, athletic helmets, hard hats, half mask respirators, full face breathing apparatus, gas masks, goggles, spectacles, face shields, hearing protectors, and ear muffs. Rinse all equipment that comes in prolonged contact with skin with warm water and allow to air dry before reuse. [Caution: Cleaning at the recommended 120ºF. temperature will avoid overheating and distortion of the personal safety equipment that would necessitate replacement.]</font></font></p>','R',NULL,6,NULL,1,NULL),(7,NULL,'<p><font face=\"Times New Roman, serif\"><font size=\"3\"><font face=\"Arial, sans-serif\">Recommended for use</font><font face=\"Arial, sans-serif\">in:</font></font></font></p>\r\n<ul>\r\n<li>\r\n<p class=\"western\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\"><font size=\"3\">Federally inspected meat and poultry plants on all hard, nonporous surfaces in inedible product processing areas, non-processing areas, and / or exterior areas. All surfaces must be thoroughly rinsed with potable water.</font></font></p>\r\n</li>\r\n</ul>\r\n<p> </p>\r\n<ul>\r\n<li>\r\n<p class=\"western\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\"><font size=\"3\">Federally inspected meat and poultry plants as a floor and wall cleaner for use in all departments. Food products and packaging material must be removed from the room or carefully protected. All surfaces must be thoroughly rinsed with potable water.</font></font></p>\r\n</li>\r\n</ul>\r\n<p> </p>\r\n<ul>\r\n<li>\r\n<p class=\"western\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\"><font size=\"3\">Federally inspected meat and poultry plants as a disinfectant agent for use in all departments. Food products and packaging materials must be removed from the room or carefully protected. Use product in accordance with its label. All surfaces must be thoroughly rinsed with potable water.</font></font></p>\r\n</li>\r\n</ul>','R',NULL,7,NULL,1,NULL),(8,NULL,'<p><font face=\"Arial, sans-serif\"><font size=\"3\">Recommended for use in tanning salons.</font></font></p>','R',NULL,8,NULL,1,NULL),(9,NULL,'<p><font face=\"Arial, sans-serif\"><font size=\"3\">Recommended for use on [tanning beds], [and] [tanning equipment]. </font></font></p>','R',NULL,9,NULL,1,NULL),(10,NULL,'<p><font face=\"Times New Roman, serif\"><font size=\"3\"><font face=\"Arial, sans-serif\"><u><strong>Formulation HWS-256</strong></u></font><font face=\"Arial, sans-serif\"><u> is effective at controlling mold and mildew odor on mattresses, pillows and shower curtains.</u></font></font></font></p>','R',NULL,10,NULL,1,NULL),(11,NULL,'<p><font face=\"Times New Roman, serif\"><font size=\"3\"><font face=\"Arial, sans-serif\"><strong>Formulation HWS-256</strong></font><font face=\"Arial, sans-serif\"> can be used in the laundry to control odors on musty bedding and linens. Test on a small inconspicuous area before using.</font></font></font></p>','R',NULL,11,NULL,1,NULL),(12,NULL,'<p><font face=\"Times New Roman, serif\"><font size=\"3\"><font face=\"Arial, sans-serif\"><strong>Formulation HWS-256</strong></font><font face=\"Arial, sans-serif\"> may be used on mildew stains </font><font face=\"Arial, sans-serif\"><u>in damp areas such as bathrooms, showers, kitchens, basements and laundry rooms. </u></font></font></font></p>','R',NULL,12,NULL,1,NULL),(13,NULL,'<p><font face=\"Times New Roman, serif\"><font size=\"3\"><font face=\"Arial, sans-serif\"><strong>Formulation HWS-256</strong></font><font face=\"Arial, sans-serif\"> may be used in work areas such as tool rooms and garages for odor control and light duty cleaning.</font></font></font></p>','R',NULL,13,NULL,1,NULL),(14,NULL,'<p><font face=\"Times New Roman, serif\"><font size=\"3\"><font face=\"Arial, sans-serif\"><strong>Formulation HWS-256</strong></font><font face=\"Arial, sans-serif\"> may be used to remove odors caused by flooding and smoke from fires.</font></font></font></p>','R',NULL,14,NULL,1,NULL),(15,NULL,'<p><font face=\"Times New Roman, serif\"><font size=\"3\"><font face=\"Arial, sans-serif\"><strong>Formulation HWS-256</strong></font><font face=\"Arial, sans-serif\"> leaves a clean fresh scent.</font></font></font></p>','R',NULL,15,NULL,1,NULL),(16,NULL,'<p><font face=\"Times New Roman, serif\"><font size=\"3\"><font face=\"Arial, sans-serif\"><strong>Formulation HWS-256</strong></font><font face=\"Arial, sans-serif\"> provides long lasting freshness against tough (pet) odors from litter boxes and pet accidents</font><font color=\"#008000\"><font face=\"Arial, sans-serif\">.</font></font></font></font></p>','R',NULL,16,NULL,1,NULL),(17,NULL,'<p style=\"margin-bottom: 0in;\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\"><font style=\"font-size: 9pt;\" size=\"2\"><em>(Fungi:)</em></font></font></p>\r\n<p style=\"margin-bottom: 0in;\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\"><font style=\"font-size: 9pt;\" size=\"2\">Aspergillus niger</font></font></p>\r\n<p style=\"margin-bottom: 0in;\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\"><font style=\"font-size: 9pt;\" size=\"2\">Candida albicans</font></font></p>\r\n<p style=\"margin-bottom: 0in;\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\"><font style=\"font-size: 9pt;\" size=\"2\">Trichophyton mentagrophytes [Athlete?s Foot Fungus]</font></font></p>\r\n<p style=\"margin-bottom: 0in;\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\"><font style=\"font-size: 9pt;\" size=\"2\">[a cause of Ringworm] [a cause of Ringworm of the foot]</font></font></p>','R',NULL,1,NULL,2,NULL),(18,NULL,'<p style=\"margin-bottom: 0in;\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\"><em>(Bacteria:)</em></font></p>\r\n<p style=\"margin-bottom: 0in;\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\">Pseudomonas aeruginosa</font><sup><font size=\"2\" face=\"Arial, sans-serif\">1</font></sup></p>\r\n<p style=\"margin-bottom: 0in;\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\">Staphylococcus aureus</font><sup><font size=\"2\" face=\"Arial, sans-serif\">1</font></sup></p>\r\n<p style=\"margin-bottom: 0in;\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\">Salmonella choleraesuis</font></p>\r\n<p style=\"margin-bottom: 0in;\" align=\"JUSTIFY\"> </p>\r\n<p style=\"margin-bottom: 0in;\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\">Acinetobacter calcoaceticus</font></p>\r\n<p style=\"margin-bottom: 0in;\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\">Bordetella bronchiseptica</font></p>\r\n<p style=\"margin-bottom: 0in;\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\">Chlamydia psittaci</font></p>\r\n<p style=\"margin-bottom: 0in;\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\">Enterobacter aerogenes</font></p>\r\n<p style=\"margin-bottom: 0in;\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\">Enterobacter cloacae</font></p>\r\n<p style=\"margin-bottom: 0in;\" align=\"JUSTIFY\"><font face=\"Arial, sans-serif\">Enterococcus faecalis ? Vancomycin Resistant (VRE)</font></p>\r\n<p